import React, { useState } from 'react';
import '../../styles/attendancecard.css';
import Calendar from './calendar'; // Import your Calendar component

export default function Attendancecard() {
  const [showCalendarOS, setShowCalendarOS] = useState(false);
  const [showCalendarAlgorithms, setShowCalendarAlgorithms] = useState(false);

  const handleOSClick = () => {
    setShowCalendarOS(true);
  };

  const handleAlgorithmsClick = () => {
    setShowCalendarAlgorithms(true);
  };

  const closeCalendarOS = () => {
    setShowCalendarOS(false);
  };

  const closeCalendarAlgorithms = () => {
    setShowCalendarAlgorithms(false);
  };

  return (
    <div className='cardholder'>    
      <section className='attendancecard' onClick={handleOSClick}>
  <div className='content'>
    <p className='SubjectName'>Operating Systems</p>
    <div className='progress'>
      <p style={{ margin: 0 + '%' }}>33%</p>
      <div className="progress-container">
        <div className="progress-bar" style={{ width: 33 + '%' }}></div>
      </div>
    </div>
    <p className='Reminder'>You need to attend 7 Lectures</p>
  </div>
</section>

<section className='attendancecard' onClick={handleAlgorithmsClick}>
  <div className='content'>
    <p className='SubjectName'>Design and Analysis of Algorithms</p>
    <div className='progress'>
      <p style={{ margin: 0 + '%' }}>88%</p>
      <div className="progress-container">
        <div className="progress-bar" style={{ width: 88 + '%' }}></div>
      </div>
    </div>
    <p className='Reminder'>You can skip 3 Lectures</p>
  </div>
</section>

      {showCalendarOS && (
        <div className="overlay" onClick={closeCalendarOS}>
          <div className="calendar-container">
            <Calendar />
          </div>
        </div>
      )}

      {showCalendarAlgorithms && (
        <div className="overlay" onClick={closeCalendarAlgorithms}>
          <div className="calendar-container">
            <Calendar />
          </div>
        </div>
      )}
    </div>
  );
}
